set title = #{title}, expireDate=#{expireDate}, price=#{price}, managementPrice=#{managementPrice},managementDesc=#{managementDesc},roomType=#{roomType},floorNow=#{floorNow},floorTotal=#{floorTotal},roomSize=#{roomSize},content=#{content},img=#{img},keyword=#{keyword},addr=#{addr},dongType=#{dongType}
where idx = #{idx}
